function cargarDatos(){var n={username:"",password:""};return n.username=$("#username").val(),n.password=$("#password").val(),n}function login(){$("#btnLogin").prop("disabled",!0);$.ajax({url:"/MainApi/Login",type:"POST",data:JSON.stringify(cargarDatos()),headers:{"Content-Type":"application/json"},success:function(n){n.error===""?window.location.href="/Main/Inicio":mostrarError("Error: "+n.error);$("#btnLogin").prop("disabled",!1)},error:function(n,t,i){mostrarError("Error: "+i);$("#btnLogin").prop("disabled",!1)}})}function mostrarError(n){$("#error").html(n);$("#mensajeError").slideDown()}$(document).ready(function(){$("#btnCloseErr").click(function(){$("#mensajeError").slideUp();$("#username").focus()});$("#mensajeError").hide();$("#username").focus();$("#btnLogin").click(login)});