function getUriParameter(n){for(var i,r=window.location.search.substr(1).split("&"),t=0;t<r.length;t++)if(i=r[t].split("="),i[0]==n)return decodeURIComponent(i[1]);return!1}function mensaje(n){$("#mensajeOk").show();$("#mensaje").html(n)}function error(n){$("#mensajeError").show();$("#error").html(n)}